CREATE PROCEDURE [dbo].[Get_OrderStatusTimeConstrained] 
AS
BEGIN
	SET NOCOUNT ON;
	SET TRANSACTION ISOLATION LEVEL READ UNCOMMITTED

	SELECT        [Order].OrderID AS WO, Customer.Name AS Customer, Part.Name AS Part, [Order].Priority,
							 [Order].CurrentLocation, dbo.fnGetCurrentProcess([Order].OrderID) AS Process, [Order].OrderType, 
							 [Order].PartQuantity, dbo.[fnGetCurrentProcessRemainingTime]([Order].OrderID) AS RemainingMinutes
	FROM            [Order] LEFT OUTER JOIN
							 Customer ON [Order].CustomerID = Customer.CustomerID LEFT OUTER JOIN
							 Part ON [Order].PartID = Part.PartID
	WHERE        ([Order].Status = N'Open') AND [Order].Hold = 0 AND dbo.[fnGetCurrentProcessRemainingTime]([Order].OrderID) IS NOT NULL
END

